const languageButtons=document.querySelectorAll(".language-switcher button"),translatableElements=document.querySelectorAll("[data-lang-ru]");function switchLanguage(e){localStorage.setItem("selectedLanguage",e),languageButtons.forEach((t=>t.classList.toggle("active",t.id===`lang-${e}`))),translatableElements.forEach((t=>{t.textContent=t.getAttribute(`data-lang-${e}`)})),document.documentElement.lang=e}function updateThemeColor(){const e=window.matchMedia("(prefers-color-scheme: dark)").matches?"#1D2025":"#F6F6F6",t=document.querySelector('meta[name="theme-color"]');t&&t.setAttribute("content",e)}document.addEventListener("DOMContentLoaded",(()=>{switchLanguage(localStorage.getItem("selectedLanguage")||"ru")})),languageButtons.forEach((e=>{e.addEventListener("click",(()=>{switchLanguage("lang-ru"===e.id?"ru":"en")}))})),document.addEventListener("DOMContentLoaded",updateThemeColor),window.matchMedia("(prefers-color-scheme: dark)").addEventListener("change",updateThemeColor);